import React from "react";

class FormElements extends React.Component {
  constructor(props) {
    super(props);
    this.state = {
      mode: "Input",
      name: "",
      eMail: "",
    };
    this.updateReactState = this.updateReactState.bind(this);
    this.displayHelloWorld = this.displayHelloWorld.bind(this);
    this.goBack = this.goBack.bind(this);
  }

  displayHelloWorld() {
    this.setState({ mode: "Display" });
  }

  updateReactState(event) {
    this.setState({ name: event.target.value });
  }

  handleInputChange(event) {
    const target = event.target;

    const name = target.name;
    this.setState({
      [name]: value    });
  }

  goBack() {
    this.setState({ mode: "Input" });
  }

  render() {
    if (this.state.mode === "Display") {
      return (
        <div>
          Name: {this.state.name}
          <button onClick={this.goBack}>goBack</button>
        </div>
      );
    }
    return (
      <div>
        <form>
          <label>Name: </label>
          <input value={this.state.name} onChange={this.updateReactState} />
          <label>Phone Number: </label>
          <input value={this.state.phone} onChange={this.updateReactState} />
          <button onClick={this.displayHelloWorld}>Submit</button>
        </form>
      </div>
    );
  }
}

export default FormElements;
